insert into book (id, author_id, description, editor_id, style, price, publication, title, version) values (1, 1, 'A quelques kilometres d''Oslo, en Norvege, l''hôpital psychiatrique de Gaustad dresse sa masse sombre parmi les pins enneigés. Appelée sur place pour un suicide, l''inspectrice Sarah Geringën pressent d''emblée que rien ne concorde. Le patient 488, ainsi surnommé suivant les chiffres cicatrisés qu''il porte sur le front, s''est figé dans la mort, un cri muet aux levres – un cri de peur primale. Soumise à un compte à rebours implacable, Sarah va découvrir une vérité vertigineuse sur l''une des questions qui hante chacun d''entre nous : la vie...', 1, 'THRILLER', 8.20, '2018-01-11', 'Le cri', 1);
insert into book (id, author_id, description, editor_id, style, price, publication, title, version) values (2, 1, 'Un archipel isolé au nord de la Norvege, battu par les vents. Et, au bord de la falaise, le corps nu et martyrisé d''une femme. Les blessures qui déchirent sa chair semblent être autant de symboles mystérieux.', 1, 'THRILLER', 9.20, '2018-05-16', 'Complot', 1);
