insert into book (id, author_id, description, editor_id, style, price, publication, title, version) values (3, 2, 'Apres L''amie prodigieuse et Le nouveau nom, Celle qui fuit et celle qui reste est la suite de la formidable saga dans laquelle Elena Ferrante raconte cinquante ans d''histoire italienne et d''amitié entre ses deux héroïnes, Elena et Lila. Pour Elena, comme pour l''Italie, une période de grands bouleversements s''ouvre. Nous sommes à la fin des années soixante, les événements de 1968 s''annoncent, les mouvements féministes et protestataires s''organisent, et Elena, diplômée de l''Ecole normale de Pise et entourée d''universitaires,...', 2, 'FICTION', 8.30, '2018-01-25', 'L''amie Prodigieuse - Tome 3 : Celle qui fuit et celle qui reste', 1);
insert into author (id, first_name, last_name, version) values (3, 'Harlan', 'Coben', 1);
insert into book (id, author_id, description, editor_id, style, price, publication, title, version) values (4, 3, 'Deux enfants kidnappés. Un inconnu qui réapparaît. Apres dix ans d''angoisse, le cauchemar ne fait que commencer...', 3, 'THRILLER', 21.90, '2018-03-01', 'Sans defense', 1);
insert into book (id, author_id, description, editor_id, style, price, publication, title, version) values (5, null, 'La Colline a été dévastée et la communauté qui l''habitait a du fuir les lieux, sous l''impulsion de Maggie. Dwight a rejoint Rick, en lui affirmant que les Chuchoteurs ont été anéantis. Malheureusement, même si Beta - qui a pris la tête des Chuchoteurs - a perdu une bataille, il lance une horde de rôdeurs sur Alexandria. La guerre est peut-être terminée, mais la survie d''Alexandria est en jeu...', 4, 'FANTASTIQUE', 14.95, '2017-10-14', 'Walking Dead - Tome 28 : Walking Dead', 1);
insert into book (id, author_id, description, editor_id, style, price, publication, title, version) values (6, null, 'Carl ne parvient pas à admettre la mort d''Andrea. Tandis que Rick fait au mieux, Maggie n''accepte pas sa décision de laisser Negan en liberté et le fait étroitement surveillé. Eugene contacte Stephanie par radio et ils conviennent de se rencontrer. A la suite de ces tragiques événements, Rick envisage d''établir une communauté dans l''Ohio. Une nouvelle ere débute pour les survivants de l''apocalypse...', 4, 'FANTASTIQUE', 14.95, '2018-03-07', 'Walking Dead - Tome 29 : La Ligne blanche', 1);
